sys_graphql_schema.schema:
schema {
    query: Query
    mutation: Mutation
}

type Query {
	#getQuestionsForQuiz(id: ID!) : [Question]
	getQuestion(id: ID!) : Question
	getQuizInstanceByAccessCode(accessCode: ID!) : QuizInstance
	getQuizInstance(id: ID!) : QuizInstance
	getQuiz(id:ID!) : Quiz
}

type Mutation {
	newUser(newUser: NewUserInput!) : String
}

input NewUserInput {
	key: ID!
	name: String!
}

type QuizInstance {
	id: ID @source(value:"sys_id.value")
	state: String @source(value:"state.display_value")
	number: String @source(value: "number.value")
	accessCode: String @source(value: "access_code.value")
	quiz: [Quiz] @source(value:"quiz.value")
}

type Quiz {
	id: ID @source(value: "sys_id.value")
	name: String @source(value: "name.value")
	number: String @source(value: "number.value")
	questions: [Question] @source(value:"sys_id.value")
}

type Question {
	id: ID @source(value:"sys_id.value")
	number: String @source(value: "number.value")
	quiz: ID @source(value: "quiz.value")
	order: Int @source(value: "order.value")
	question: String @source(value: "question.value")
	correctAnswer: ID @source(value:"correct_answer.value")
	choices: [Choice] @source(value:"sys_id.value")
}

type Choice {
	id: ID @source(value: "sys_id.value")
	number: String @source(value: "number.value")
	text: String @source(value: "choice.value")
	isCorrectAnswer: Boolean @source(value: "correct_answer.value")
	order: Int @source(value:"order.value")
}
